    private void usuariosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_usuariosActionPerformed
        switch(usuarios.getSelectedIndex()){
            case 1:
                fotos.setIcon(new javax.swing.ImageIcon(getClass().getResource("/imagenes/user1.png")));
                NombreUsuario = (String) usuarios.getSelectedItem();
                break;
                
            case 2:
                fotos.setIcon(new javax.swing.ImageIcon(getClass().getResource("/imagenes/user2.png")));
                NombreUsuario = (String) usuarios.getSelectedItem();
                break;
                
            case 3:
                fotos.setIcon(new javax.swing.ImageIcon(getClass().getResource("/imagenes/user3.png")));
                NombreUsuario = (String) usuarios.getSelectedItem();
                break;
            
            
        }
        {
            System.out.println(NombreUsuario);
        }
    }//GEN-LAST:event_usuariosActionPerformed

