    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String password = String.valueOf(lpass.getPassword()).trim();
       
        if(lusuarios.equals(lusuarios) && password.equals("Admin")){
                this.setVisible(false);
                gestor G = new gestor ();
                G.setVisible(true);

        }else if(lusuarios.equals(lusuarios) && password.equals("123")){
                this.setVisible(false);
                gestor G = new gestor ();
                G.setVisible(true);
                
        }else if(lusuarios.equals(lusuarios) && password.equals("456")){
                this.setVisible(false);
                gestor G = new gestor ();
                G.setVisible(true);
                
        }else {System.out.println("Acceso denegado, ingrese correctamente la contraseña");
                this.setVisible(false);
                accesono G = new accesono ();
                G.setVisible(true);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

